import React from 'react';
import { connect } from 'react-redux';
import { fetchTodos } from '../actions';
import { Link } from 'react-router-dom';

class TodoList extends React.Component {
	componentDidMount() {
		this.props.fetchTodos();
	}
	renderAdmin(todo) {
		if (todo.userId === this.props.currentUserId) {
			return (
				<div className='right floated content'>
					<Link to={`/todos/edit/${todo.id}`} className='ui button primary'>
						Edit
					</Link>
					<Link to={`/todos/delete/${todo.id}`} className='ui button negative'>
						Delete
					</Link>
				</div>
			);
		}
	}

	renderList() {
		if (this.props.todos !== '') {
			return this.props.todos.map(todo => {
				return (
					<div className='item' key={todo.id}>
						{this.renderAdmin(todo)}
						<div className='content'>
							<Link to={`/todos/${todo.id}`} className='header'>
								{todo.title}
							</Link>
							<div className='description'>{todo.description}</div>
						</div>
					</div>
				);
			});
		} else if (this.props.todos !== []) {
			return (
				<div style={{ textAlign: 'center', color: ' #ff6680' }}>
					<h2>
						You have no tasks in your bucket list. Add some tasks to get your
						day started!
					</h2>
				</div>
			);
		}
	}

	renderCreate() {
		if (this.props.isSignedIn) {
			return (
				<div style={{ textAlign: 'right' }}>
					<Link to='/todos/new' className='ui button primary'>
						Add a Task
					</Link>
				</div>
			);
		}
	}

	render() {
		return (
			<div>
				<h2 style={{ textAlign: 'center' }}>Your tasks for the day:</h2>
				<div className='ui celled list'>{this.renderList()}</div>
				{this.renderCreate()}
			</div>
		);
	}
}

const mapStateToProps = state => {
	return {
		todos: Object.values(state.todos),
		currentUserId: state.auth.userId,
		isSignedIn: state.auth.isSignedIn,
	};
};

export default connect(mapStateToProps, { fetchTodos })(TodoList);
